import React, { Component } from 'react';

import './App.css';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {

    event.preventDefault();
    nimi = this.state.value;
    console.error(nimi);
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {klikkaus}

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    klikkaus++;
    this.setState(state => klikkaus);
  }

  render() {
    if(nimi == undefined){
      return (
        <NameForm />
      )
    }
    else {
    let voitto;
  if((klikkaus%50) === 0){
    voitto = <h1>ISO palkinto!</h1>;
  }
  else if((klikkaus%20) === 0){
    voitto = <h1>Keskikokoinen palkinto!</h1>;
  }
  else if((klikkaus%10) === 0){
    voitto = <h1>Pieni palkinto!</h1>;
  }
  else{
    voitto = <p>Seuraavaan voittoon {10-(klikkaus%10)} klikkausta</p>
  }


    return (
      <div>
      {voitto}
      <button onClick={this.handleClick}>
        Klikkaa ja voita
      </button>
      </div>

    );
  }
}
}

let klikkaus = 1;
let nimi;

export default App;
