import React, { Component } from 'react';

function Palkinto (props){
    let palkintoteksti;
    let palkinto;
    if((props%50) === 0){
        palkintoteksti = <h1>ISO palkinto!</h1>;
        palkinto = 1;
      }
      else if((props%20) === 0){
        palkintoteksti = <h1>Keskikokoinen palkinto!</h1>;
        palkinto = 1;
      }
      else if((props%10) === 0){
        palkintoteksti = <h1>Pieni palkinto!</h1>;
        palkinto = 1;
      }
      else{
        palkintoteksti = <p>Seuraavaan palkintoon {10-(props%10)} klikkausta</p>;
        palkinto = 0;
      }
      return [palkintoteksti, palkinto];
}

function Listaus(props) {
    
    const voittajat = props;
    const listItems = voittajat.map((voittaja, index) =>
      <li key={index}>
        {voittaja}
      </li>
    );
    return (
      <ul>{listItems}</ul>
    );
  }

class Game extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          klikkaus: 1, 
          voittajalista: [], 
          voittajat: []
        };
        
      this.handleClick = this.handleClick.bind(this);
    }
  
    handleClick() {
      
      this.setState(state => this.state.klikkaus++);
      palkinnot = Palkinto(this.state.klikkaus);
      if(palkinnot[1] == 1){
          voittajalista.push(this.props.nimi);
          this.setState(state => voittajalista);
      }
      console.log(this.state);
    }

    componentDidMount() {
        fetch('/users')
          .then(res => res.json())
          .then(voittajat => this.setState({ voittajat }));
          console.log(this.state);
      }
  
    render() {
    
    if(voittajalista.length > 0){
       lista = Listaus(voittajalista);
    }
    
      return (
        <div>
            {this.props.nimi}
        {palkinnot[0]}
        <button onClick={this.handleClick}>
          Klikkaa ja voita
        </button>
        {lista}
        </div>
       
        
      );
    
  }
  }
  
  let palkinnot = [];
  let voittajalista = [];
  let lista;
 
  
  export default Game;
