import React, { Component } from 'react';
import Game from './Game';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class Name extends React.Component {
    constructor(props) {
      super(props);
      this.state={
        value:''
      }
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
        var self = this;
        
        if(self.state.value == ''){
            alert('Syötä nimimerkki');
            event.preventDefault();
        }
        else{
            event.preventDefault();
            var GameScreen=[];
            GameScreen.push(<Game key='Game' appContext={self.props.appContext} nimi={self.state.value}/>)
            self.props.appContext.setState({NamePage:[],GameScreen:GameScreen})
        }
    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <TextField
          id="nimi"
          label="Anna nimimerkki"
          value={this.state.value}
          onChange={this.handleChange}
          margin="normal"
        />
        <Button type="submit" value="Submit" variant="contained" color="primary">Lähetä</Button>
          
        </form>
      );
    }
  }

  export default Name;
