import React from 'react';
import Game from './Game';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';
import CssBaseline from '@material-ui/core/CssBaseline';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';

const styles = theme => ({
	Button: {
		marginBottom: '7px',
		marginLeft: '5px',
		verticalAlign: 'bottom'

	},
	Modal: {
		position: 'absolute',
		width: '400px',
		height: '200px',
		background: '#64b5f6',
		boxShadow: theme.shadows[5],
		verticalAlign: 'center',
		textAlign: 'center',
		padding: '45px',
		outline: 'none',
		borderStyle: 'solid',
  		borderColor: 'red'
	  },


});

class Name extends React.Component {
    constructor(props) {
      super(props);
      this.state={
        value:'',
        open:false

      }
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
    handleClose = () => {
      this.setState({ open: false });
    };
  
    handleSubmit(event) {
        var self = this;
        
        if(self.state.value === ''){
          this.setState({ open: true });
            event.preventDefault();
        }
        else{
            event.preventDefault();
            var GameScreen=[];
            GameScreen.push(<Game key='Game' appContext={self.props.appContext} nimi={self.state.value}/>)
            self.props.appContext.setState({NamePage:[],GameScreen:GameScreen})
        }
    }
  
    render() {
		const { classes } = this.props;
      return (
        <div>
			<CssBaseline />
        <Modal
			
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div className={classes.Modal}>
            <h2>Syötä nimimerkki</h2>
          </div>
        </Modal>
        <h2>Aloita antamalla nimimerkkisi</h2>
        <div>
        <form onSubmit={this.handleSubmit}>
          <TextField
          id="nimi"
          label="Nimimerkki"
          value={this.state.value}
          onChange={this.handleChange}
          margin="normal"
        />
        <Button className={classes.Button} type="submit" value="Submit" variant="contained" color="primary">Lähetä</Button>
          
        </form>
		</div>
        </div>
      );
    }
  }

  Name.propTypes = {
	classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(Name);
