import React, { Component } from 'react';
import Button from '@material-ui/core/Button';

function Palkinto (props){
	let palkintoteksti;
	let palkinto;
	if((props%50) === 0){
		palkintoteksti = <h1>ISO palkinto!</h1>;
		palkinto = 1;
	}
	else if((props%20) === 0){
		palkintoteksti = <h1>Keskikokoinen palkinto!</h1>;
		palkinto = 1;
	}
	else if((props%10) === 0){
		palkintoteksti = <h1>Pieni palkinto!</h1>;
		palkinto = 1;
	}
	else{
		palkintoteksti = <p>Seuraavaan palkintoon {10-(props%10)} klikkausta</p>;
		palkinto = 0;
	}
	return [palkintoteksti, palkinto];
}

function Listaus(props) {
	const voittajat = props;
	const listItems = voittajat.map((voittaja, index) =>
		<li key={index}>
			{voittaja}
		</li>
	);
	return (
		<ul>{listItems}</ul>
	);
}


function countEdit(props) {
	var data = {
		luku: props,
		id: '1'
	}
	fetch('/count/edit', {
		method: 'POST',
		headers: {
		'Content-Type': 'application/json'
		},
		body: JSON.stringify(data)
	}).then((response) => {
		if(!response.ok) throw new Error(response.status);
		else return response.json();
	}).catch((error) => {
		console.log('error: ' + error);
	});
}


function addWinner(props){
	var data = {
		nimi: props
	}
	fetch("/winners/winner", {
		method: 'POST',
		headers: {'Content-Type': 'application/json'},
		body: JSON.stringify(data)
	}).then((response) => {
		if(!response.ok) throw new Error(response.status);
		else return response.json();
	}).catch((error) => {
		console.log('error: ' + error);
	});
}

class Game extends React.Component {
	constructor(props) {
	super(props);
	this.state = {
		klikkaus: [], 
		voittajalista: [], 
		voittajat: [],
		countRequestFailed: false,
		lastModified: ''
	};

	this.handleClick = this.handleClick.bind(this);
	}

	componentDidMount() {
		fetch('/winners')
		.then((response) => {
			if(!response.ok) throw new Error(response.status);
			else return response.json();
		}).then((data) => {
			this.setState({ voittajat: data });
		}).catch((error) => {
			console.log('error: ' + error);
		});

		this.timerID = setInterval(
			() => this.tick(),
			10000
		);
	}

	componentWillUnmount() {
		clearInterval(this.timerID);
	}

	tick() {
		fetch('/winners')
		.then((response) => {
			if(!response.ok) throw new Error(response.status);
			else return response.json();
		}).then((data) => {
			this.setState({ voittajat: data });
		}).catch((error) => {
			console.log('error: ' + error);
		});
		klikattu = 0;
	}


	handleClick() {
		klikattu = 1;
		fetch('/count', {
		method: 'GET',
		headers: {'Content-Type': 'application/json', 
		'If-Modified-Since': this.state.lastModified 
		}}).then((response) => {
			if(!response.ok) throw new Error(response.status);
			else if(response.status === 304) throw new Error(response.status);
			else return response.json();
		}).then((data) => {
			let date = new Date(Date.now());
			this.setState({ countRequestFailed: false, lastModified: date.toUTCString(), klikkaus: data });
		}).catch((error) => {
			console.log('error: ' + error);
			this.setState({ countRequestFailed: true });
		});
	}




	render() {

		if(klikattu == 1 && this.state.klikkaus.length > 0 && this.state.countRequestFailed == false){
			let luku;
			luku = this.state.klikkaus[0].luku+1;
			console.log(luku);
			console.log("lisäsin");
			palkinnot = Palkinto(luku);
			countEdit(luku);
			if(palkinnot[1] == 1){
				addWinner(this.props.nimi);
			}
			klikattu = 0;
		}
		return (
			<div>
				<h3>Nimimerkkisi on {this.props.nimi}</h3>
				<fade>{palkinnot[0]}</fade>
				<Button variant="contained" color="primary" onClick={this.handleClick}>
				Klikkaa ja voita
				</Button>
				<h1>Edelliset voittajat:</h1>
				{this.state.voittajat.map(voittajat =>
				<div key={voittajat.id}>{voittajat.nimi}</div>
				)}
			</div>
		);
	}
}

let palkinnot = [];
let klikattu = 0;


export default Game;
