import React, { Component } from 'react';
import Game from './Game';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';

class Name extends React.Component {
    constructor(props) {
      super(props);
      this.state={
        value:'',
        open:false

      }
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
        var self = this;
        
        if(self.state.value == ''){
          this.setState({ open: true });
            event.preventDefault();
        }
        else{
            event.preventDefault();
            var GameScreen=[];
            GameScreen.push(<Game key='Game' appContext={self.props.appContext} nimi={self.state.value}/>)
            self.props.appContext.setState({NamePage:[],GameScreen:GameScreen})
        }
    }
  
    render() {
      return (
        <div>
        <Modal
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div style={getModalStyle()} className={classes.paper}>
            <Typography variant="h6" id="modal-title">
              Text in a modal
            </Typography>
            <Typography variant="subtitle1" id="simple-modal-description">
              Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </Typography>
            <SimpleModalWrapped />
          </div>
        </Modal>
        <form onSubmit={this.handleSubmit}>
          <TextField
          id="nimi"
          label="Anna nimimerkki"
          value={this.state.value}
          onChange={this.handleChange}
          margin="normal"
        />
        <Button type="submit" value="Submit" variant="contained" color="primary">Lähetä</Button>
          
        </form>
        </div>
      );
    }
  }

  export default Name;
