import React, { Component } from 'react';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {klikkaus}

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    klikkaus++;
    console.log(klikkaus);
  }

  render() {
    let voitto;
  if((klikkaus%50) === 0){
    voitto = <h1>ISO palkinto!</h1>;
  }
  else if((klikkaus%20) === 0){
    voitto = <h1>Keskikokoinen palkinto!</h1>;
  }
  else if((klikkaus%10) === 0){
    voitto = <h1>Pieni palkinto!</h1>;
  }


    return (
      <div>
      {voitto}
      {klikkaus}
      <button onClick={this.handleClick}>
        Klikkaa ja voita
      </button>
      </div>

    );
  }
}

let klikkaus = 0;

export default App;
