import React, { Component } from 'react';

function Palkinto (props){
    let palkintoteksti;
    let palkinto;
    if((props%50) === 0){
        palkintoteksti = <h1>ISO palkinto!</h1>;
        palkinto = 1;
      }
      else if((props%20) === 0){
        palkintoteksti = <h1>Keskikokoinen palkinto!</h1>;
        palkinto = 1;
      }
      else if((props%10) === 0){
        palkintoteksti = <h1>Pieni palkinto!</h1>;
        palkinto = 1;
      }
      else{
        palkintoteksti = <p>Seuraavaan palkintoon {10-(props%10)} klikkausta</p>;
        palkinto = 0;
      }
      return [palkintoteksti, palkinto];
}

function Listaus(props) {
    
    const voittajat = props;
    const listItems = voittajat.map((voittaja, index) =>
      <li key={index}>
        {voittaja}
      </li>
    );
    return (
      <ul>{listItems}</ul>
    );
  }

  function HaeLuku(props) {
 
      fetch('/count')
        .then(res => { return res.json()});
        
  }

class Game extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          klikkaus: [], 
          voittajalista: [], 
          voittajat: []
        };
        
      this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        fetch('/users')
          .then(res => res.json())
          .then(voittajat => this.setState({ voittajat: voittajat }));
          
      }
  
    handleClick() {
      fetch('/count')
          .then(res => res.json())
          .then(klikkaus => this.setState({ klikkaus: klikkaus }));
    }
          
          handleEdit(props) {
            var data = {
                luku: props
            }
            fetch("/users/edit", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            }).then(function(response) {
                if (response.status >= 400) {
                    throw new Error("Bad response from server");
                }
                return response.json();
            }).then(function(data) {
                console.log(data)
                if (data === "success") {
                    this.setState({
                        msg: "User has been edited."
                    });
                }
            }).catch(function(err) {
                console.log(err)
            });
          }
      
      /*
      if(palkinnot[1] == 1){
          voittajalista.push(this.props.nimi);
          this.setState(state => voittajalista);
      }
      */  
    

    
  
    render() {
    
    /*if(this.state.voittajat.length > 0){
       console.log(this.state.voittajat);
        //lista = Listaus(this.state.voittajat);
    }
    */
   console.log(this.state.klikkaus);
   if(this.state.klikkaus.length > 0){
    let luku;
    luku = this.state.klikkaus[0].luku+1;
    console.log(JSON.stringify(this.state.klikkaus[0].luku+1));
    palkinnot = Palkinto(luku);
    handleEdit(luku);
    }
      return (
        <div>
            {this.props.nimi}
        {palkinnot[0]}
        <button onClick={this.handleClick}>
          Klikkaa ja voita
        </button>
        <h1>Edelliset voittajat:</h1>
        {this.state.voittajat.map(voittajat =>
            <div key={voittajat.id}>{voittajat.nimi}</div>
          )}
        </div>
       
        
      );
    
  }
  }
  
  let palkinnot = [];
  let voittajalista = [];
  let lista;
 
  
  export default Game;
